    SELECT id, idperfil INTO _id_usuario, _perfil_actual FROM cat_login_usuarios WHERE usuario = _correo;
    
    SET _id_usuario = ifnull(_id_usuario, 0);
    
    IF _clave = "no" THEN
		IF _id_usuario = 0 THEN
			SELECT concat("Ocurrió un error y no se encuentra el correo ", _correo) msg, -1 response;
        ELSE 
			UPDATE cat_login_usuarios SET
				idperfil = _perfil,
				cambio_clave = 0,
				estado = _estado
			WHERE id = _id_usuario;
            
            IF _perfil_actual = _perfil THEN
