			INSERT INTO cat_login_usuarios (
				usuario,
				clave,
                token,
				tipo_inicio,
				id_pais,
				id_ciudad,
				id_punto,
				idperfil,
				cambio_clave,
				estado
			) VALUES (
				_correo,
				_clave,
                '',
                'e-mail',
				7,
				_id_ciudad,
				_id_punto,
				_perfil,
				0,
				2
			);
    
			SET _id_usuario = last_insert_id();
            
            IF _perfil = 4 THEN
				INSERT INTO reg_cliente (
					id_usuario,
					nombre,
					telefono_fijo,
					telefono_celular,
                    direccion,
                    barrio,
					correo
				) VALUES (
					_id_usuario,
					_nombre,
					_fijo,
					_celular,
                    _addres,
                    _barrio,
					_correo
				);
			ELSE 
				INSERT INTO reg_aliado (
					id_usuario,
					nombre,
					telefono_fijo,
					telefono_celular,
                    direccion,
                    barrio,
					correo
				) VALUES (
					_id_usuario,
