CREATE DEFINER=`integration`@`%` PROCEDURE `valida_correo`(IN _correo text)
BEGIN
	DECLARE _estado, _id integer;
    
	SELECT id, estado INTO _id, _estado FROM cat_login_usuarios WHERE usuario =  _correo;
    
    SET _estado = ifnull(_estado, -1);
    
    CASE _estado
		/*
			estados:
            0: error/no existe
            1: correcto
            2: pendiente de validacióno
            3: desactivado/bloqueado
        */
		WHEN -1 THEN
			SELECT concat("El correo ", _correo," no esta registrado en nuestro sistema") msg, -1 response;
		WHEN 0 THEN
			SELECT concat("El correo ", _correo," no esta registrado en nuestro sistema") msg, 0 response;
		WHEN 1 THEN
			SELECT concat("Este enlace se encuentra inactivo <br/>El correo ", _correo," ya fue validado!") msg, 1 response;
		WHEN 2 THEN
			UPDATE cat_login_usuarios SET
				estado = 1
			WHERE id = _id;
        
			SELECT concat("Gracias por inscribirte en nuestra aplicación ya puedes iniciar sesión con el correo ", _correo) msg, 2 response;
		WHEN 3 THEN
			SELECT concat("La cuenta perteneciente al correo ", _correo,"  se encuentra desactivada!") msg, 3 response;
	END CASE;
END ;;
