CREATE DEFINER=`integration`@`%` PROCEDURE `verifica_con_clave`(IN _correo text,
    IN _clave text,
    IN _token text)
BEGIN
	DECLARE _id, _perfil, _estado integer;
    DECLARE _correo_restaurante, _direccion, _lat_lng, _ciudad, _barrio text;
    
	SELECT id, idperfil, estado INTO _id, _perfil, _estado FROM cat_login_usuarios WHERE usuario = _correo AND clave = _clave;
    
    SET _estado = ifnull(_estado, -1);
    
    CASE _estado
		/*
			estados:
            0: error/no existe
            1: correcto
            2: pendiente de validacióno
            3: desactivado/bloqueado
        */
			WHEN -1 THEN
				SELECT id INTO _id FROM cat_login_usuarios WHERE usuario = _correo;
                SET _id = ifnull(_id, -1);
                IF _id = -1 THEN 
					SELECT concat("El correo ", _correo, " no se encuentra registrado con nosotros!\n\nPrimero debes registrarte\nTambien puedes iniciar sesión con Google o Facebook") msg, _estado estado, -1 response;
                ELSE 
					SELECT "La clave es incorrecta!\n Intenta de nuevo" msg, _estado estado, -1 response;
                END IF;
            WHEN 0 THEN 
				SELECT email INTO _correo_restaurante FROM cat_punto_venta;
				SELECT concat("Esta cuenta se encuentra desactivada, ponte en contacto con ", _correo_restaurante, " para reactivarla  ") msg, _estado estado, 0 response;
			WHEN 1 THEN
				SELECT id_ciudad, direccion, barrio, lat_lng_direccion INTO _ciudad, _direccion, _barrio, _lat_lng FROM reg_mis_direcciones WHERE  id_usuario = _id AND direccion_actual = 1;
            
				IF _perfil = 4 THEN
					# si es un cliente retorna por aquí
					SELECT *, (SELECT descripcion FROM cat_perfil WHERE id_perfil = idperfil) as perfil,
                    _ciudad ciudad, 'Colombia' pais,
                    _direccion direccion, _barrio barrio, _lat_lng lat_lng
                    FROM cat_login_usuarios, reg_cliente WHERE id_usuario = id AND id = _id;
                ELSE
					# si es administrador retorna por aquí
					SELECT *, (SELECT descripcion FROM cat_perfil WHERE id_perfil = idperfil) as perfil,
                    _ciudad ciudad, 'Colombia' pais,
                    _direccion direccion, _barrio barrio, _lat_lng lat_lng
                    FROM cat_login_usuarios, reg_aliado WHERE id_usuario = id AND id = _id;
				END IF;
                
                # guarda la fecha de login en formato unix
                UPDATE cat_login_usuarios SET 
					token = _token,
					fecha_ultimo_ingreso = unix_timestamp()
				WHERE id = _id;
            WHEN 2 THEN 
				SELECT concat("El correo '", _correo, "' esta pendiente de validación, ingresa a la bandeja de tu correo y validado pulsando en “valida aquí”") msg, _estado estado, 2 response;			
	END CASE;
END ;;
